import React, { useState,useEffect, useContext } from "react"
import { Outlet,Link, Navigate } from "react-router-dom";
import { AuthContext } from "../../../context/auth.context";
import { UserContext } from "../../../context/user.context";
import './header.styles.scss'
function Header(){
    const [title,setTitle]=useState(" ");
    const [fullTitle] = useState("Library Management")
    const [index,setIndex] = useState(0);
    const [under,setUnder]=useState("")
    const {auth,setAuth}=useContext(AuthContext)
    const {curUser,setUser} = useContext(UserContext)
    useEffect(()=>{
        if(index<fullTitle.length){
            setTimeout(()=>{
                setTitle(title+fullTitle[index])
                setIndex(index+1)
                if(under==="")
                setUnder("_")
                else setUnder("")
            },500)
        }
    },[index,title,fullTitle,under])

    function handleSignOut(){
        setAuth(false);
        setUser({
            name:"",
            role:"",
            email:"",
            password:""})
        return <Navigate to="/" replace={true}/>    }
    return (<div className="">
        <header><Link className="nav-link" to="/">
        <div className="heading"><h1>{title}{under}</h1></div>
        </Link>
        <div className="nav-links-container">
        {curUser.role==="admin"?<Link className="nav-link" to ="/admin">Admin Portal</Link>:
        <Link className="nav-link" to="">Dashboard</Link>
    }
        {auth===false?
        <Link className="nav-link" to="/signin">SignIn</Link>:
        <Link onClick={handleSignOut} className="nav-link" to="/">Sign out</Link>
        }
        </div></header>
        <div></div>
        {auth==true?<h5>Hello {curUser.name}</h5>:null}
        <Outlet/>
        </div>
      )
}

export default Header;
