import React, { useContext } from "react";
import {BookContext} from '../../../context/books.context'
import { Link ,Outlet} from "react-router-dom";

export default function AvailableBooks(){
    const {curBooks} = useContext(BookContext)
    return <div>{curBooks.map((props)=>{
        props.status===1 && props.rented===false?
        <div className="book grid-item" >
    <h3>{props.title}</h3>
    <p>{props.subtitle}</p>
    <p>Author: {props.author}</p>
    <p>Published on: {props.published.substring(0,10)}</p>
    <Link to={`/bookdetail/${props.isbn}`}>Know more
    </Link>
    <Outlet/>
    <button onClick={event=>rent(event,props.isbn)}>Rent</button>
    </div>:null})}
    </div>
}
